xwikidoc.content:
import com.xpn.xwiki.web.Utils;
import org.xwiki.wiki.template.WikiTemplateManager;

public void create(newWikiId, newWikiAlias, templateId, ownerId, failOnExist) {
  WikiTemplateManager wikiTemplateManager= Utils.getComponentManager().getInstance(WikiTemplateManager.class);
  wikiTemplateManager.createWikiFromTemplate(newWikiId, newWikiAlias, templateId, ownerId, failOnExist);
}
